package javapack;

public class SwitchCaseexaxmpl {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		switch("opera") {
		
		case "firefox":
			System.out.println("open firefox script");
		break;
		case "chrome":
			System.out.println("open chrome script");
		break;
		case "ie":
			System.out.println("open ie script");
		break;
		case "safari":
			System.out.println("open safari script");
		break;
		default:
			System.out.println("browser not avilable");
		
		
		}
		
		
		
		

	}

}
